sp_widget.client_script:
function() {
  /* widget controller */
  var c = this;

	c.cases = c.data.cases;
	c.selectedCase = function(id) {
		//alert(id);
		var obj= {
			action: 'CaseId',
			id : id
		};
		$rootScope.$broadcast('customEvent', obj);
	}
}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

		data.cases = [];
	var gr = new GlideRecord('x_264406_it_servic_case');
	gr.query();
	while(gr.next()) {
		data.cases.push({
			number : gr.getValue('number'),
			short_description : gr.getDisplayValue('short_description'),
			description : gr.getDisplayValue('description'),
			sys_id : gr.getUniqueValue()
		});

	}
		return;
})();
