sp_widget.client_script:
function() {
  /* widget controller */
  var c = this;
			$scope.$on('customEvent', function(event,obj) {
		c.data.sys_id = obj.id;
		c.data.action = 'getCase';
		c.server.update().then(function(r){
			console.log(r);
			c.cases = angular.copy(r.cases)
		});
	});

}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

	data.cases = {};
	if(input) {
		if(input.action == 'getCase') {
			data.sys_id = input.sys_id;

			var gr = new GlideRecord('x_264406_it_servic_case');
			if(gr.get(data.sys_id)) {
				data.cases = {
					number : gr.getValue('number'),
					short_description : gr.getDisplayValue('short_description'),
					description : gr.getDisplayValue('description'),
					priority: gr.getDisplayValue('priority'),
					category: gr.getDisplayValue('category'),
					assigned_to: gr.getDisplayValue('assigned_to'),
					state :gr.getDisplayValue('state'),
					sys_id : gr.getUniqueValue()
				};	
			}
		}
	}

})();
